function onSubmit(){const e=document.getElementById("yourRank").value;const t=document.getElementById("yourGun").value;const n=document.getElementById("theirRank").value;const r=document.getElementById("theirArmour").value;const a=document.getElementById("theirHealth").value;const s=calculate(e,t,n,r,a);const i=document.getElementById("resultArea");const o=document.getElementById("result");if(s){o.innerText=`It will take ${Math.ceil(s)} bullets to kill your target`}else{o.innerText="Something went wrong with the calculation"}i.hidden=false}const RANKS={hobo:1,civilian:2,recruit:3,pioneer:4,respectablePioneer:5,businessman:6,respectableBusinessman:7,spy:8,hitman:9,respectableHitman:10,assassin:11,respectableAssassin:12,factionManager:13,respectableFactionManager:14,mafioso:15,respectableMafioso:16,boss:17,respectableBoss:18,godfather:19,infamousGodfather:20,don:21,respectableDon:22,infamousGangster:23};const GUNS={fnM240b:1};const ARMOUR={none:.1,swatVest:1,lightMiltary:1.25,mediumMilitary:1.5,heavyMilitary:2};const getRankValue=e=>{if(e in RANKS){return RANKS[e]}return undefined};const getGunMultiplier=e=>{if(e in GUNS){return GUNS[e]}return 0};const getArmourMultiplier=e=>{if(e in ARMOUR){return ARMOUR[e]}return 0};const calculate=(e,t,n,r,a)=>{const s=getRankValue(e);if(!s){return 0}const i=getRankValue(n);if(!i){return 0}const o=18500*(i/s);const l=getGunMultiplier(t);if(!l){return 0}const u=getArmourMultiplier(r);if(!u){return 0}try{const c=parseInt(a)/100;const m=o*l*c;const d=Math.ceil(m)*u;return Math.ceil(d)}catch(e){return 0}};
